let links=document.querySelectorAll(".links li a");links.forEach(e=>{e.addEventListener("click",e=>{e.preventDefault(),document.querySelector("."+e.target.dataset.section).scrollIntoView({behavior:"smooth"}),links.forEach(e=>{e.classList.remove("active")}),e.target.classList.add("active")})});let btn=document.querySelector(".scroll i");btn.addEventListener("click",e=>{document.querySelector("header").scrollIntoView({behavior:"smooth"})}),window.onscroll=function(e){document.documentElement.scrollTop<=700?btn.parentNode.style.display="none":btn.parentNode.style.display="block"};